targets.expr:
label_replace(
  label_join(
    max by (switch_hostname, interface) (
      rate(nvswitch_interface_pg_rx_buffer_discard{
        switch_hostname=~"$hostname",
        pg="1",
        cluster="$cluster"
      }[$__rate_interval])
    ),
    "join_key", " | ", "switch_hostname", "interface"
  )
  unless
  label_join(
    max by (switch_hostname, interface) (
      max without(port_id, port_description) (
        nvswitch_lldp_neighbor_info{
          switch_hostname=~"$hostname",
          cluster="$cluster",
          system_name=~".+"
        }
      )
    ),
    "join_key", " | ", "switch_hostname", "interface"
  ),
  "system_name", "LLDP missing", "join_key", ".*"
)
or
label_replace(
  label_join(
    max by (switch_hostname, interface) (
      rate(nvswitch_interface_pg_rx_buffer_discard{
        switch_hostname=~"$hostname",
        pg="1",
        cluster="$cluster"
      }[$__rate_interval])
    ),
    "join_key", " | ", "switch_hostname", "interface"
  )
  * on(join_key)
  group_left(system_name)
  label_join(
    max by (switch_hostname, interface, system_name) (
      max without(port_id, port_description) (
        nvswitch_lldp_neighbor_info{
          switch_hostname=~"$hostname",
          cluster="$cluster",
          system_name=~".+"
        }
      )
    ),
    "join_key", " | ", "switch_hostname", "interface"
  ),
  "system_name", "$1", "system_name", "(.*)"
)

